/*
 * Resolve the GET request to a filename and it's content type
 * msg.filename - the filename to be read and sent to client
 * msg.topic - the Content-Type to be sent in the header
 * msg.statusCode - set to default 200, OK
 * msg.headers - initialize headers to empty object {}
 */

// Use string object in our function
var S = function(x) {
  return msg.string.setValue(x);
}

// Check for invalid urlPath
var nrg = flow.get("nrg");
msg.statusCode = 200;
if (!msg.req.url.startsWith(nrg.urlPath)) {
    msg.statusCode = 403;
    node.send(msg);
    return;
}

// Obtain filepath
var file = S(msg.req.url).delLeftMost(nrg.urlPath)
           .prepend(nrg.siteRoot);
var ext = file.getRightMost("/");

// Missing file extension and last slash, 301 to folder/
if (ext.indexOf(".") == -1 && file.substr(file.length -1) != "/") {
    msg.statusCode = 301;
    msg.headers = {
        Location: "http://" + msg.req.headers.host + S(msg.req.url).ensureRight("/").toString()
    }
    delete msg.payload;
    node.send(msg);
    return;
}

// Missing file extension, assume folder and "/index.nrg"
if (ext.indexOf(".") == -1) {
    ext = "nrg";
    msg.req.url = S(msg.req.url).ensureRight("/").toString();
    file = file.ensureRight("/").concat("index.nrg");
}else{
    ext = ext.getRightMost(".").s;
}
msg.filename = file.s;

// Prevent forbidden extensions
if (nrg.forbidden.indexOf(ext) != -1) {
    msg.statusCode = 403;
    node.send(msg);
    return;
}

// Callback hell; find the file or try index.nrg, index.html, index.htm
var af = global.get("actionflows");
var p = af.invoke("#getFile", msg);
p.then(function(msg) {
    if (typeof msg.error != "undefined") {
        msg.statusCode = 404;
        if (msg.filename.endsWith("index.nrg")) {

            // Try index.html
            var s = S(msg.filename);
            msg.filename = s.delRightMost("index.nrg").concat("index.html").s;
            delete msg.error;
            p = af.invoke("#getFile", msg);
            p.then(function(msg) {
                if (typeof msg.error != "undefined") {

                    // Try index.htm
                    var s = S(msg.filename);
                    msg.filename = s.delRightMost("index.html").concat("index.htm").s;
                    delete msg.error;
                    p = af.invoke("#getFile", msg);
                    p.then(function(msg) {
                        if (typeof msg.error != "undefined") {
                            msg.statusCode = 404;
                            node.send(msg);
                        }else{
                            msg.statusCode = 200;
                            node.send(msg);
                        }
                    });
                }else{
                    msg.statusCode = 200;
                    node.send(msg);
                }
            });
        }else{
            msg.statusCode = 200;
            node.send(msg);
        }
    }else{
        node.send(msg);
    }
});

// Include nrg, actionflows
var af = global.get("actionflows");
var nrg = flow.get("nrg");

// Use string object in our function
var S = function(x) {
  return msg.string.setValue(x);
};

// Process redirect
if (msg.statusCode == 301) {
    node.send([msg, null]);
    return;
}

// Get Content-Type by extension to mime-types
var ext = S(msg.filename).getRightMost(".").s;
msg.topic = "text/html";
for (var key in nrg.mimeTypes) {
    if (nrg.mimeTypes[key].indexOf(ext) != -1) {
        msg.topic = key;
        break;
    }
}

// Process error templates
if (msg.statusCode != 200) {
    var p = af.invoke("#" + msg.statusCode + "error", msg);
    p.then(function(msg) {
        node.send([msg, null]);
    });
}else{

    // Process .nrg files
    if (ext == "nrg") {
        node.send([null, msg]);
    }else{
        node.send([msg, null]);
    }
}

// Use string object in our function
var S = function(x) {
  return msg.string.setValue(x);
}

// Parse out HTML comments
nrgtag = [];
msg.payload = S(msg.payload);
while (msg.payload.indexOf('<nrg_') > -1) {
    msg.payload = msg.payload.delLeftMost('<nrg_');
    var action = msg.payload.getLeftMost('>').getLeftMost(' ').toString();
    var selfclosing = (msg.payload.getLeftMost('>').indexOf('selfclosing') > -1)
    if (selfclosing) {
        msg.payload = msg.payload.delLeftMost('/>');
    }else{
        msg.payload = msg.payload.delLeftMost('</nrg_' + action + '>');
    }
    nrgtag.push({"action": action, "selfclosing": selfclosing});
}
msg.payload = "";

// Invoke nrgFlows
msg.outputBuffer = "";
var af = global.get("actionflows");
var nrg = flow.get("nrg");
function nrgFlows() {
    if (nrgtag.length == 0) {
        msg.outputBuffer += msg.payload + msg.pre;
        msg.payload = msg.outputBuffer.substr(5, msg.outputBuffer.length - 11);
        delete msg.outputBuffer;
        delete msg.pre;
        node.send(msg);
        return;
    }
    var n = nrgtag.shift();
    msg.outputBuffer += msg.pre.getLeftMost("<nrg_" + n.action);
    msg.pre = msg.pre.delLeftMost("<nrg_" + n.action);

    if (n.selfclosing) {
        msg.pre = msg.pre.delLeftMost("/>");
    }else{
        // Obtain block for payload
        msg.pre = msg.pre.delLeftMost(">");
        n.payload = msg.pre.getLeftMost("</nrg_" + n.action + ">").s;
        msg.pre = msg.pre.delLeftMost("</nrg_" + n.action + ">");
    }
    msg = Object.assign(msg, n);
    delete msg.action;
    if (n.action == "script") {
        n.action = "#" + n.action;
        delete msg.filename;
        if (n.selfclosing) {
            var file = S(n.payload).replaceAll("/", nrg.slash).replaceAll("\\", nrg.slash);
            if (file.left(1) != nrg.slash) {
                file = file.prepend(nrg.siteRoot + nrg.slash).s;
            }
            msg.filename = file;
            msg.evalScript = "";
            msg.payload = "";
        }else{
            msg.evalScript = n.payload;
            msg.payload = "";
        }
    }
    var p = af.invoke(n.action, msg);
    p.then(function(msg) {
        // Report any errors
        if (msg.statusCode != 200) {
            var p = af.invoke("#" + msg.statusCode + "error", msg);
            p.then(function(msg) {
                node.send(msg);
            });
            return;
        }
        if (nrgtag.length > 0) {
            msg.outputBuffer += msg.payload;
            nrgFlows();
        }else{
            msg.outputBuffer += msg.payload + msg.pre;
            msg.payload = msg.outputBuffer.substr(5, msg.outputBuffer.length - 11);
            delete msg.outputBuffer;
            delete msg.pre;
            node.send(msg);
        }
    });
}
nrgFlows();

// Preprocess nrg files, convert short format syntax to
// XML compatible with attributes

// Use string object in our function
var S = function(x) {
  return msg.string.setValue(x);
};

var chunks = S(msg.payload).replaceAll("<#", "<nrg_").replaceAll("</#", "</nrg_").splitLeft("<nrg_", -1);
var result = "";
if (typeof msg.includes == "undefined") {
    msg.includes = {};
}
while(chunks.length > 0) {
    var chunk = S(chunks.shift());
    // TODO: replace dirty hack; match brackets ignoring quoted string
    var tag = chunk.getLeftMost(String.fromCharCode(10))
        .replaceAll('">"', '~|~').getLeftMost(">").append(">")
        .replaceAll('~|~', '">"'); // This overcomes until=">" scenario
    chunk = chunk.delLeftMost(tag);
    // Identify self-closing
    if (tag.right(2) == "/>" && tag.indexOf("selfclosing") == -1) {
        tag = S(tag.slice(0, -2).trim() + " selfclosing=\"true\" />");
    }

    // Expand short format
    if (tag.indexOf("(") != -1 && tag.indexOf(");") != -1) {
        var before = tag.getLeftMost("(");
        var value = tag.delLeftMost("(").getLeftMost(");");
        if (value.length !== 0) {
            value = value.ensureLeft('"').ensureRight('"');
        }else{
            value = '""';
        }
        var after = tag.delLeftMost(");");
        tag = before + " payload=" + value + after;
    }
    if (tag.indexOf("include ") != -1) {
        var file = S(tag).delLeftMost('payload="').getLeftMost('"');
        if (typeof msg.includes[file] == "undefined") {
            msg.includes[file] = null;
        }
    }
    chunk = tag + chunk;
    result = result + chunk;
    if (chunks.length > 0) {
         result += "<nrg_";
    }else{
        msg.payload = result;
        node.send(msg);
    }    
}

// check that includes are obtained
// check if includes is in payload, if so, replace it with code and 
// set msg.loop = true else false

// Use string object in our function
var S = function(x) {
  return msg.string.setValue(x);
};
var af = global.get("actionflows");
var nrg = flow.get("nrg");

// Read all include files
var preads = [];
for (var key in msg.includes) {
    if (msg.includes[key] === null) {
        var file = S(key).replaceAll("/", nrg.slash).replaceAll("\\", nrg.slash);
        if (file.left(1) != nrg.slash && file.charAt(2) != ":") {
            file = file.prepend(nrg.siteRoot + nrg.slash).s;
        }
        var m = { filename: file, payload: "" };
        preads.push(af.invoke("#getFile", m).then(function(m) {
            if (typeof m.error == "undefined") {
                msg.includes[key] = String(m.payload);
            }else{
                msg.error = m.error;
                node.error(msg.error.message);
                msg.statusCode = 500;
            }
        }));
    }
}

// Swap out all includes
Promise.all(preads).then(function() {
    var result = S(msg.payload);
    for (var key in msg.includes) {
        var tag = '<nrg_include payload="' + key + '" selfclosing="true" />';
        result = result.replaceAll(tag, msg.includes[key]);
    }
    msg.payload = result.s;

    if (msg.payload.indexOf("<#include") == -1) {
        msg.pre = S(msg.payload); // preload looks too much like payload
        msg.loop = false;
    }else{
        msg.loop = true;
    }
    node.send(msg);
});

// Execute inline script block
if (typeof msg.filename == "undefined") {
    return eval("(function(){" + msg.evalScript + "})();");

// Obtain JavaScript server-side (.jss)
}else{
    var af = global.get("actionflows");
    var p = af.invoke("#getFile", msg);
    p.then(function(msg) {
        if (typeof msg.error == "undefined") {
            msg.evalScript = msg.payload;
            msg.payload = "";
            var result = eval("(function(){" + msg.evalScript + "})();");
            if (typeof result != "undefined") {
                node.send(result);
            }
        }else{
            node.error(msg.error.message);
            msg.statusCode = 500;
            node.send(msg);
        }
    });
}
